jQuery(document).ready(function(){BUGSPATROL_STORAGE.media_frame=null;BUGSPATROL_STORAGE.media_link="";jQuery(".bugspatrol_media_selector").on("click",function(a){bugspatrol_show_media_manager(this);a.preventDefault();return false})});function bugspatrol_show_media_manager(a){BUGSPATROL_STORAGE.media_link=jQuery(a);if(BUGSPATROL_STORAGE.media_frame){BUGSPATROL_STORAGE.media_frame.open();return false}BUGSPATROL_STORAGE.media_frame=wp.media({title:BUGSPATROL_STORAGE.media_link.data("choose"),library:{type:"image"},multiple:BUGSPATROL_STORAGE.media_link.data("multiple")===true?"add":false,button:{text:BUGSPATROL_STORAGE.media_link.data("update"),close:true}});BUGSPATROL_STORAGE.media_frame.on("select",function(b){var c=jQuery("#"+BUGSPATROL_STORAGE.media_link.data("linked-field")).eq(0);var e="";if(BUGSPATROL_STORAGE.media_link.data("multiple")===true){BUGSPATROL_STORAGE.media_frame.state().get("selection").map(function(f){e+=(e?"\n":"")+f.toJSON().url});var d=c.val();e=d+(d?"\n":"")+e}else{e=BUGSPATROL_STORAGE.media_frame.state().get("selection").first().toJSON().url}c.val(e);if(c.siblings("img").length>0){c.siblings("img").attr("src",e)}c.trigger("change")});BUGSPATROL_STORAGE.media_frame.open();return false};